mapper.select:
SELECT si.strength, ps.vote_num
FROM perfumes as p
JOIN perfume_sillage as ps ON p.id = ps.perfume_id JOIN sillage_info as si ON ps.sillage_info_id = si.id
WHERE p.id = #{id}
;

SELECT p.id as perfume_id, COUNT(*) as accord_match_count FROM perfumes p
JOIN perfume_main_accords as pma ON p.id = pma.perfume_id JOIN accords as a ON pma.accord_id = a.id
WHERE EXISTS ( SELECT 1 FROM determined_notes dn WHERE dn.perfume_id = p.id ) AND a.name IN
#{item}
GROUP BY p.id ORDER BY accord_match_count DESC LIMIT 20 ;

SELECT u.user_id, n.name, n.type
FROM user as u JOIN user_notes as un ON u.id = un.user_id JOIN notes as n ON un.note_id = n.id
WHERE u.user_id = #{userId} ;

SELECT a.id AS accord_id, a.name AS accord, SUM(ma.weight) AS totalWeight FROM mood as m JOIN mood_accord as ma ON m.id = ma.mood_id JOIN accords as a ON ma.accord_id = a.id WHERE m.id IN
#{item}
GROUP BY a.id, a.name ORDER BY totalWeight DESC LIMIT 12

mapper.insert:
INSERT INTO user_notes (user_id, note_id, user_note_gram, memo)
SELECT u.id, n.id, #{userNoteGram}, #{memo}
FROM `user` AS u
JOIN notes AS n ON n.name = #{note}
WHERE u.user_id = #{userId}
LIMIT 1;

mapper.delete:
DELETE un
FROM user_notes AS un
JOIN `user` AS u ON u.id = un.user_id
JOIN notes  AS n ON n.id = un.note_id
WHERE u.user_id = #{userId}
  AND n.name    = #{note};

mapper.select:
	SELECT p.id
    FROM perfumes AS p
    JOIN determined_notes AS dn ON dn.perfume_id = p.id
    JOIN notes AS n ON dn.note_id = n.id
    WHERE n.name IN

        #{note}

    GROUP BY p.id
    HAVING COUNT(DISTINCT n.name) = #{listSize}
    LIMIT 500
	;

SELECT p.id as match_id , COUNT(DISTINCT n.name) as match_count , JSON_ARRAYAGG(n.name) as matched_notes_json FROM perfumes as p JOIN determined_notes as dn ON p.id = dn.perfume_id JOIN notes as n ON dn.note_id = n.id WHERE n.name IN
#{note}
GROUP BY match_id HAVING COUNT(DISTINCT n.name) >= #{minCount} ORDER BY COUNT(DISTINCT n.name) DESC, p.id ASC LIMIT 500 ;
